mapper.select:
SELECT * FROM lab_results 
WHERE patient_id = #{patientId} 
AND test_type = #{testType}
AND test_time BETWEEN #{startTime} AND #{endTime}
ORDER BY test_time

mapper.insert:
INSERT INTO lab_results(
    patient_id, test_time, test_type,
    parameter_name, value, unit, reference_range
) VALUES (
    #{patientId}, #{testTime}, #{testType},
    #{parameterName}, #{value}, #{unit}, #{referenceRange}
)

INSERT INTO lab_results(
    patient_id, test_time, test_type,
    parameter_name, value, unit, reference_range
) VALUES 

    (
        #{result.patientId}, #{result.testTime}, #{result.testType},
        #{result.parameterName}, #{result.value}, #{result.unit}, #{result.referenceRange}
    )
